import React, { useState } from 'react';

const MinesweeperBoard = ({ rows, cols, mines }) => {
    const [board, setBoard] = useState(generateBoard(rows, cols, mines));

    function generateBoard(rows, cols, mines) {
        // Initialize the board with empty cells
        const board = Array.from({ length: rows }, () => Array(cols).fill({ mine: false, revealed: false }));

        // Place mines randomly
        let placedMines = 0;
        while (placedMines < mines) {
            const row = Math.floor(Math.random() * rows);
            const col = Math.floor(Math.random() * cols);
            if (!board[row][col].mine) {
                board[row][col].mine = true;
                placedMines++;
            }
        }

        return board;
    }

    function handleCellClick(row, col) {
        // Reveal the cell and update the board state
        const newBoard = [...board];
        newBoard[row][col].revealed = true;
        setBoard(newBoard);
    }

    return (
        <div className="minesweeper-board">
            {board.map((row, rowIndex) => (
                <div key={rowIndex} className="minesweeper-row">
                    {row.map((cell, colIndex) => (
                        <div
                            key={colIndex}
                            className={`minesweeper-cell ${cell.revealed ? 'revealed' : ''}`}
                            onClick={() => handleCellClick(rowIndex, colIndex)}
                        >
                            {cell.revealed && cell.mine ? 'ðŸ’£' : ''}
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default MinesweeperBoard;
