import React, { useEffect } from 'react';
import '@dotlottie/player-component'; // Import the dotlottie-player component

const WelcomeScreen = ({ joinGame }) => {
    useEffect(() => {
        // Ensure the dotlottie-player component is defined
        if (!customElements.get('dotlottie-player')) {
            customElements.define('dotlottie-player', DotLottiePlayer);
        }
    }, []);

    return (
        <div className="hero center" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <h3>Welcome to the</h3>
            <h1>Retro Retro Game</h1>
            <br />
            <dotlottie-player 
                src="https://lottie.host/a4c5d72a-3c2a-4d1a-9e3f-d2d955ff653f/IQdCcDyTiR.lottie" 
                background="transparent" 
                speed="1" 
                style={{ maxWidth: '20em', maxHeight: '20em' }} 
                loop={1} 
                autoplay 
            />
            <br />
            <button className="cta" onClick={joinGame}>Join a Game</button>
        </div>
    );
};

export default WelcomeScreen;
