import React from 'react';

const GameStateEnd = ({ scores, winner, onNewGame, onReturnToTitle }) => {
    return (
        <div>
            <h2>Game Over</h2>
            <h3>Winner: {winner}</h3> {/* Display the winner */}
            <div>
                <h3>Scores:</h3>
                <ul>
                    {Object.entries(scores).map(([player, score]) => (
                        <li key={player}>{player}: {score}</li>
                    ))}
                </ul>
            </div>
            <button onClick={onNewGame}>New Game</button> {/* New Game button */}
            <button onClick={onReturnToTitle}>Return to Title Screen</button> {/* Return to Title Screen button */}
        </div>
    );
};

export default GameStateEnd;
